conbody.codeblock:
CREATE TABLE kudu_tbl1
(
 id INT NON UNIQUE PRIMARY KEY,
 name STRING
)
PARTITION BY HASH (id) PARTITIONS 3 STORED as KUDU;

example.codeblock:
CREATE TABLE auto_table
(
 a BIGINT,
 b STRING,
)
PARTITION BY HASH(a, b) PARTITIONS 2 STORED AS KUDU;

conbody.codeblock:
CREATE TABLE pk_inline
(
  col1 BIGINT PRIMARY KEY,
  col2 STRING,
  col3 BOOLEAN
) PARTITION BY HASH(col1) PARTITIONS 2 STORED AS KUDU;

CREATE TABLE pk_at_end
(
  col1 BIGINT,
  col2 STRING,
  col3 BOOLEAN,
  PRIMARY KEY (col1)
) PARTITION BY HASH(col1) PARTITIONS 2 STORED AS KUDU;

CREATE TABLE pk_inline
(
col1 BIGINT [NON UNIQUE] PRIMARY KEY,
col2 STRING,
col3 BOOLEAN
) PARTITION BY HASH(col1) PARTITIONS 2 STORED AS KUDU;

CREATE TABLE pk_at_end
(
col1 BIGINT,
col2 STRING,
col3 BOOLEAN,
[NON UNIQUE] PRIMARY KEY (col1)
) PARTITION BY HASH(col1) PARTITIONS 2 STORED AS KUDU;

-- 50 buckets, all for IDs beginning with a lowercase letter. -- Having only a single range enforces the allowed range of values -- but does not add any extra parallelism. create table million_rows_one_range (id string primary key, s string) partition by hash(id) partitions 50, range (partition 'a' <= values < '{') stored as kudu;
-- 50 buckets for IDs beginning with a lowercase letter -- plus 50 buckets for IDs beginning with an uppercase letter. -- Total number of buckets = number in the PARTITIONS clause x number of ranges. -- We are still enforcing constraints on the primary key values -- allowed in the table, and the 2 ranges provide better parallelism -- as rows are inserted or the table is scanned. create table million_rows_two_ranges (id string primary key, s string) partition by hash(id) partitions 50, range (partition 'a' <= values < '{', partition 'A' <= values < '[') stored as kudu;
-- Same as previous table, with an extra range covering the single key value '00000'. create table million_rows_three_ranges (id string primary key, s string) partition by hash(id) partitions 50, range (partition 'a' <= values < '{', partition 'A' <= values < '[', partition value = '00000') stored as kudu;
-- The range partitioning can be displayed with a SHOW command in impala-shell. show range partitions million_rows_three_ranges; +---------------------+ | RANGE (id) | +---------------------+ | VALUE = "00000" | | "A" <= VALUES < "[" | | "a" <= VALUES < "{" | +---------------------+

--- Make a table representing a date/time value as TIMESTAMP. -- The strings representing the partition bounds are automatically -- cast to TIMESTAMP values. create table native_timestamp(id bigint, when_exactly timestamp, event string, primary key (id, when_exactly)) partition by hash (id) partitions 20, range (when_exactly) ( partition '2015-01-01' <= values < '2016-01-01', partition '2016-01-01' <= values < '2017-01-01', partition '2017-01-01' <= values < '2018-01-01' ) stored as kudu;
insert into native_timestamp values (12345, now(), 'Working on doc examples');
select * from native_timestamp; +-------+-------------------------------+-------------------------+ | id | when_exactly | event | +-------+-------------------------------+-------------------------+ | 12345 | 2017-05-31 16:27:42.667542000 | Working on doc examples | +-------+-------------------------------+-------------------------+

-- now() returns a TIMESTAMP and shows the format for string literals you can cast to TIMESTAMP. select now(); +-------------------------------+ | now() | +-------------------------------+ | 2017-01-25 23:50:10.132385000 | +-------------------------------+
-- unix_timestamp() accepts either a TIMESTAMP or an equivalent string literal. select unix_timestamp(now()); +------------------+ | unix_timestamp() | +------------------+ | 1485386670 | +------------------+
select unix_timestamp('2017-01-01'); +------------------------------+ | unix_timestamp('2017-01-01') | +------------------------------+ | 1483228800 | +------------------------------+
-- Make a table representing a date/time value as BIGINT. -- Construct 1 range partition and 20 associated hash partitions for each year. -- Use date/time conversion functions to express the ranges as human-readable dates. create table time_series(id bigint, when_exactly bigint, event string, primary key (id, when_exactly)) partition by hash (id) partitions 20, range (when_exactly) ( partition unix_timestamp('2015-01-01') <= values < unix_timestamp('2016-01-01'), partition unix_timestamp('2016-01-01') <= values < unix_timestamp('2017-01-01'), partition unix_timestamp('2017-01-01') <= values < unix_timestamp('2018-01-01') ) stored as kudu;
-- On insert, we can transform a human-readable date/time into a numeric value. insert into time_series values (12345, unix_timestamp('2017-01-25 23:24:56'), 'Working on doc examples');
-- On retrieval, we can examine the numeric date/time value or turn it back into a string for readability. select id, when_exactly, from_unixtime(when_exactly) as 'human-readable date/time', event from time_series order by when_exactly limit 100; +-------+--------------+--------------------------+-------------------------+ | id | when_exactly | human-readable date/time | event | +-------+--------------+--------------------------+-------------------------+ | 12345 | 1485386696 | 2017-01-25 23:24:56 | Working on doc examples | +-------+--------------+--------------------------+-------------------------+

