[localhost:21000] > select y from t2 where exists (select x from all_nulls);
+---+
| y |
+---+
| 2 |
| 4 |
| 6 |
+---+

[localhost:21000] > create table empty (x int);
[localhost:21000] > select y from t2 where exists (select x from empty);
