create table sample_demo_partitions (x int, s string) partitioned by (n int) stored as parquet;
insert into sample_demo_partitions partition (n = 1) select * from sample_demo; insert into sample_demo_partitions partition (n = 2) select * from sample_demo; insert into sample_demo_partitions partition (n = 3) select * from sample_demo;
